import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Graph.css';
import AdjacencyMatrix from './AdjacencyMatrix';
import dfsAlgorithm from './dfsAlgorithm';

function Graph() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [nodesState, setNodesState] = useState([]);
  const [message, setMessage] = useState('');

  // Функції для додавання вершини
  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }
    const nodeId = nodes.length + 1;
    const newNode = {
      id: nodeId,
      label: `V${nodeId}`,
      x: Math.random() * 700 + 50,
      y: Math.random() * 500 + 50,
      state: 'unvisited',
    };
    setNodes([...nodes, newNode]);
  };

  // Функція для видалення вершини
  const deleteNode = (nodeId) => {
    setNodes(nodes.filter((node) => node.id !== nodeId));
    setEdges(edges.filter((edge) => edge.from !== nodeId && edge.to !== nodeId));
  };

  const addEdge = (fromId, toId) => {
    const fromNode = nodes.find((node) => node.id === fromId);
    const toNode = nodes.find((node) => node.id === toId);
  
    if (!fromNode || !toNode) {
      alert('Вершина з таким ID не знайдена');
      return;
    }
  
    if (edges.filter((edge) => edge.from === fromId).length >= 14) {
      alert('Максимальна кількість ребер з цієї вершини досягнута');
      return;
    }
  
    const newEdge = { from: fromId, to: toId };
    setEdges([...edges, newEdge]);
  };
  

  // Функція для видалення ребра
  const deleteEdge = (fromId, toId) => {
    setEdges(edges.filter((edge) => !(edge.from === fromId && edge.to === toId)));
  };

  // Функція для відображення графу
  const renderGraph = () => {
    return (
      <svg width="800" height="600">
        {/* Ребра */}
        {edges.map((edge, index) => {
          const fromNode = nodes.find((node) => node.id === edge.from);
          const toNode = nodes.find((node) => node.id === edge.to);
          if (fromNode && toNode) {
            return (
              <line
                key={index}
                x1={fromNode.x}
                y1={fromNode.y}
                x2={toNode.x}
                y2={toNode.y}
                stroke="black"
                markerEnd="url(#arrowhead)"
              />
            );
          }
          return null;
        })}
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="10"
            refY="3.5"
            orient="auto"
          >
            <polygon points="0 0, 10 3.5, 0 7" />
          </marker>
        </defs>
        {/* Вершини */}
        {nodes.map((node) => (
          <g key={node.id}>
            <circle
              cx={node.x}
              cy={node.y}
              r="20"
              fill={
                node.state === 'unvisited'
                  ? 'gray'
                  : node.state === 'processing'
                  ? 'green'
                  : 'black'
              }
            />
            <text
              x={node.x}
              y={node.y + 5}
              textAnchor="middle"
              fill="white"
              fontSize="15"
            >
              {node.label}
            </text>
          </g>
        ))}
      </svg>
    );
  };

  // Запуск алгоритму DFS
  const startDFS = async () => {
    if (nodes.length === 0) {
      alert('Граф порожній');
      return;
    }
    const startNodeLabel = prompt('Введіть початкову вершину (наприклад, V1):');
    const startNode = nodes.find((node) => node.label === startNodeLabel);
    if (!startNode) {
      alert('Початкова вершина не знайдена');
      return;
    }
    setIsRunning(true);
    setMessage('Виконується алгоритм DFS...');
    await dfsAlgorithm(nodes, edges, startNode.id, setNodesState);
    setIsRunning(false);
    setMessage('Алгоритм завершено');
  };

  useEffect(() => {
    if (nodesState.length > 0) {
      setNodes(nodesState);
    }
  }, [nodesState]);

  return (
    <div className="graph-container">
      <h1>Алгоритм Глибокого Пошуку (DFS)</h1>
      <div className="graph-area">
        {renderGraph()}
      </div>
      <AdjacencyMatrix nodes={nodes} edges={edges} />
      <div className="controls">
        <button onClick={addNode}>Додати Вершину</button>
        <button
          onClick={() => {
            const from = parseInt(prompt('З якої вершини (ID)?'));
            const to = parseInt(prompt('До якої вершини (ID)?'));
            addEdge(from, to);
          }}
        >
          Додати Ребро
        </button>
        <button onClick={startDFS} disabled={isRunning}>
          Start
        </button>
        <button onClick={() => setIsRunning(false)} disabled={!isRunning}>
          Stop
        </button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
      <p>{message}</p>
    </div>
  );
}

export default Graph;
