// src/components/Graph/Graph.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import AdjacencyMatrix from './AdjacencyMatrix/AdjacencyMatrix';
import EdgeList from './EdgeList/EdgeList';
import './Graph.css';

const Graph = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [currentNode, setCurrentNode] = useState(null);
  const [targetNode, setTargetNode] = useState(null);
  const [stepHistory, setStepHistory] = useState([]);
  const [representation, setRepresentation] = useState('matrix');
  const [dfsState, setDfsState] = useState(null);

  // Фіксовані позиції для 15 вершин
  const predefinedPositions = [
    { x: 400, y: 100 }, // V1
    { x: 300, y: 200 }, // V2
    { x: 500, y: 200 }, // V3
    { x: 250, y: 300 }, // V4
    { x: 400, y: 300 }, // V5
    { x: 550, y: 300 }, // V6
    { x: 200, y: 400 }, // V7
    { x: 300, y: 400 }, // V8
    { x: 400, y: 400 }, // V9
    { x: 500, y: 400 }, // V10
    { x: 600, y: 400 }, // V11
    { x: 200, y: 500 }, // V12
    { x: 300, y: 500 }, // V13
    { x: 400, y: 500 }, // V14
    { x: 500, y: 500 }, // V15
  ];

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = [
      { id: 1, label: 'V1', x: predefinedPositions[0].x, y: predefinedPositions[0].y, state: 'unvisited' },
      { id: 2, label: 'V2', x: predefinedPositions[1].x, y: predefinedPositions[1].y, state: 'unvisited' },
      { id: 3, label: 'V3', x: predefinedPositions[2].x, y: predefinedPositions[2].y, state: 'unvisited' },
      { id: 4, label: 'V4', x: predefinedPositions[3].x, y: predefinedPositions[3].y, state: 'unvisited' },
      { id: 5, label: 'V5', x: predefinedPositions[4].x, y: predefinedPositions[4].y, state: 'unvisited' },
      { id: 6, label: 'V6', x: predefinedPositions[5].x, y: predefinedPositions[5].y, state: 'unvisited' },
    ];

    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];

    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }

    const newNodeId = nodes.length + 1;

    // Визначаємо позицію для нового вузла з фіксованих координат
    const position = predefinedPositions[newNodeId - 1];
    if (!position) {
      alert('Немає визначеної позиції для нової вершини.');
      return;
    }

    const newNode = {
      id: newNodeId,
      label: `V${newNodeId}`,
      x: position.x,
      y: position.y,
      state: 'unvisited',
    };

    setNodes((prev) => [...prev, newNode]);
  };

  const deleteNode = () => {
    const nodeId = parseInt(prompt('Введіть ID вершини для видалення:'), 10);
    if (isNaN(nodeId) || !nodes.some((node) => node.id === nodeId)) {
      alert('Некоректний ID вершини');
      return;
    }

    setNodes((prev) => prev.filter((node) => node.id !== nodeId));
    setEdges((prev) => prev.filter((edge) => edge.from !== nodeId && edge.to !== nodeId));
  };

  const addEdge = () => {
    const fromId = parseInt(prompt('Введіть ID вихідної вершини:'), 10);
    const toId = parseInt(prompt('Введіть ID цільової вершини:'), 10);

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !nodes.some((node) => node.id === fromId) ||
      !nodes.some((node) => node.id === toId)
    ) {
      alert('Некоректні ID вершин');
      return;
    }

    // Перевіряємо, чи вже існує таке ребро
    if (edges.some((edge) => edge.from === fromId && edge.to === toId)) {
      alert('Таке ребро вже існує.');
      return;
    }

    setEdges((prev) => [...prev, { from: fromId, to: toId }]);
  };

  const deleteEdge = () => {
    const fromId = parseInt(prompt('Введіть ID вихідної вершини для видалення ребра:'), 10);
    const toId = parseInt(prompt('Введіть ID цільової вершини для видалення ребра:'), 10);

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !edges.some((edge) => edge.from === fromId && edge.to === toId)
    ) {
      alert('Ребро не знайдено');
      return;
    }

    setEdges((prev) => prev.filter((edge) => !(edge.from === fromId && edge.to === toId)));
  };

  const startDFS = () => {
    if (isRunning) return;

    const startNodeId = parseInt(prompt('Введіть ID початкової вершини:'), 10);
    const endNodeId = parseInt(prompt('Введіть ID кінцевої вершини:'), 10);

    if (
      isNaN(startNodeId) ||
      isNaN(endNodeId) ||
      !nodes.some((node) => node.id === startNodeId) ||
      !nodes.some((node) => node.id === endNodeId)
    ) {
      alert('Некоректний ID вершини');
      return;
    }

    setIsRunning(true);
    setIsPaused(false);
    setTargetNode(endNodeId);

    const visited = new Set();
    const stack = [startNodeId];

    setStepHistory([]);
    setDfsState({
      stack,
      visited,
      currentNode: null,
    });
  };

  const advanceDFS = useCallback(async () => {
    if (!dfsState || isPaused || !isRunning) return;

    let { stack, visited } = dfsState;

    if (stack.length === 0) {
      // DFS завершено
      setIsRunning(false);
      setCurrentNode(null);
      return;
    }

    const current = stack.pop();

    setCurrentNode(current);

    if (current === targetNode) {
      // Знайдено цільову вершину
      setNodes((prev) =>
        prev.map((node) =>
          node.id === current ? { ...node, state: 'target' } : node
        )
      );
      alert(`Знайдено кінцеву вершину V${targetNode}`);
      setIsRunning(false);
      setCurrentNode(null);
      return;
    }

    // Встановлюємо стан 'processing'
    setNodes((prev) =>
      prev.map((node) =>
        node.id === current ? { ...node, state: 'processing' } : node
      )
    );

    // Затримка 2 секунди для візуалізації
    await new Promise((resolve) => setTimeout(resolve, 2000));

    // Встановлюємо стан 'visited'
    setNodes((prev) =>
      prev.map((node) =>
        node.id === current ? { ...node, state: 'visited' } : node
      )
    );

    // Додаємо вершину до відвіданих
    visited = new Set(visited);
    visited.add(current);

    // Додаємо сусідів до стеку
    const neighbors = edges
      .filter((edge) => edge.from === current && !visited.has(edge.to))
      .map((edge) => edge.to);

    neighbors.forEach((neighbor) => {
      if (!visited.has(neighbor)) {
        stack.push(neighbor);
      }
    });

    setStepHistory((prev) => [...prev, current]);

    // Оновлюємо стан DFS
    setDfsState({
      stack,
      visited,
      currentNode: current,
    });
  }, [dfsState, isPaused, isRunning, edges, targetNode]);

  useEffect(() => {
    if (isRunning && !isPaused && dfsState) {
      advanceDFS();
    }
  }, [dfsState, isRunning, isPaused, advanceDFS]);

  const pauseDFS = () => {
    setIsPaused((prevPaused) => !prevPaused);
  };

  const undoDFS = () => {
    if (stepHistory.length === 0) {
      alert('Немає попередніх кроків для відкату.');
      return;
    }

    const newHistory = [...stepHistory];
    const lastVisitedNode = newHistory.pop();
    const previousNode = newHistory[newHistory.length - 1] || null;

    // Оновлюємо стан вершин
    setNodes((prev) =>
      prev.map((node) => {
        if (node.id === lastVisitedNode) return { ...node, state: 'unvisited' };
        if (node.id === previousNode) return { ...node, state: 'processing' };
        return node;
      })
    );

    setCurrentNode(previousNode);

    // Оновлюємо стан DFS
    setDfsState((prevState) => {
      const { stack, visited } = prevState;

      const newVisited = new Set(visited);
      newVisited.delete(lastVisitedNode);

      const newStack = [...stack, lastVisitedNode];

      return {
        ...prevState,
        stack: newStack,
        visited: newVisited,
        currentNode: previousNode,
      };
    });

    setStepHistory(newHistory);
  };

  const resetGraph = () => {
    setNodes((prev) =>
      prev.map((node) => ({
        ...node,
        state: 'unvisited',
      }))
    );
    setStepHistory([]);
    setCurrentNode(null);
    setTargetNode(null);
    setIsRunning(false);
    setIsPaused(false);
    setDfsState(null);
  };

  const renderGraph = () => (
    <svg width="800" height="600">
      {edges.map((edge, index) => {
        const fromNode = nodes.find((node) => node.id === edge.from);
        const toNode = nodes.find((node) => node.id === edge.to);
        if (!fromNode || !toNode) return null;
        return (
          <line
            key={index}
            x1={fromNode.x}
            y1={fromNode.y}
            x2={toNode.x}
            y2={toNode.y}
            stroke="black"
            markerEnd="url(#arrowhead)"
          />
        );
      })}
      <defs>
        <marker
          id="arrowhead"
          markerWidth="10"
          markerHeight="7"
          refX="10"
          refY="3.5"
          orient="auto"
        >
          <polygon points="0 0, 10 3.5, 0 7" />
        </marker>
      </defs>
      {nodes.map((node) => (
        <g key={node.id}>
          <circle
            cx={node.x}
            cy={node.y}
            r="20"
            fill={
              node.id === targetNode
                ? 'green'
                : node.state === 'unvisited'
                ? 'gray'
                : node.state === 'processing'
                ? 'orange'
                : node.state === 'visited'
                ? 'black'
                : 'gray'
            }
          />
          <text
            x={node.x}
            y={node.y + 5}
            textAnchor="middle"
            fill="white"
            fontSize="15"
          >
            {node.label}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="graph-container">
      <h1>Алгоритм Пошуку в Глибину (DFS)</h1>
      <div className="graph-area">{renderGraph()}</div>
      <div className="representation-controls">
        <button onClick={() => setRepresentation('matrix')}>
          Матриця Суміжності
        </button>
        <button onClick={() => setRepresentation('edges')}>
          Список Ребер
        </button>
      </div>
      {representation === 'matrix' && (
        <AdjacencyMatrix nodes={nodes} edges={edges} />
      )}
      {representation === 'edges' && <EdgeList edges={edges} />}
      <div className="controls">
        <button onClick={addNode}>Додати Вершину</button>
        <button onClick={deleteNode}>Видалити Вершину</button>
        <button onClick={addEdge}>Додати Ребро</button>
        <button onClick={deleteEdge}>Видалити Ребро</button>
        <button onClick={startDFS}>Старт</button>
        <button onClick={pauseDFS} disabled={!isRunning}>
          {isPaused ? 'Продовжити' : 'Пауза'}
        </button>
        <button onClick={undoDFS} disabled={!isRunning}>
          Назад
        </button>
        <button onClick={resetGraph}>Забути пошук</button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
      {currentNode && <p>Поточна вершина: V{currentNode}</p>}
    </div>
  );
};

export default Graph;
