// src/components/Graph/Graph.jsx

import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import Controls from './Controls';
import Visualization from './Visualization';
import RepresentationControls from './RepresentationControls';
import CurrentNode from './CurrentNode';
import AdjacencyMatrix from './adjacencyMatrix/AdjacencyMatrix';
import EdgeList from './edgeList/EdgeList';
import { depthFirstSearch } from './algorithms/depthFirstSearch';
import './Graph.css';

const Graph = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [currentNode, setCurrentNode] = useState(null);
  const [targetNode, setTargetNode] = useState(null);
  const [representation, setRepresentation] = useState('matrix');

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = [
      { id: 1, label: 'V1', x: 200, y: 100, state: 'unvisited' },
      { id: 2, label: 'V2', x: 400, y: 100, state: 'unvisited' },
      { id: 3, label: 'V3', x: 600, y: 100, state: 'unvisited' },
      { id: 4, label: 'V4', x: 200, y: 200, state: 'unvisited' },
      { id: 5, label: 'V5', x: 400, y: 200, state: 'unvisited' },
      { id: 6, label: 'V6', x: 600, y: 200, state: 'unvisited' },
    ];

    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];

    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }

    const newNodeId = nodes.length + 1;
    const rows = Math.ceil(newNodeId / 3);
    const cols = (newNodeId - 1) % 3 + 1;
    const newX = 200 + (cols - 1) * 200;
    const newY = 70 + (rows - 1) * 120;

    const newNode = {
      id: newNodeId,
      label: `V${newNodeId}`,
      x: newX,
      y: newY,
      state: 'unvisited',
    };

    setNodes((prev) => [...prev, newNode]);
  };

  const deleteNode = () => {
    const nodeId = parseInt(prompt('Введіть ID вершини для видалення:'), 10);
    if (isNaN(nodeId) || !nodes.some((node) => node.id === nodeId)) {
      alert('Некоректний ID вершини');
      return;
    }

    setNodes((prev) => prev.filter((node) => node.id !== nodeId));
    setEdges((prev) =>
      prev.filter((edge) => edge.from !== nodeId && edge.to !== nodeId)
    );
  };

  const addEdge = () => {
    const fromId = parseInt(prompt('Введіть ID вихідної вершини:'), 10);
    const toId = parseInt(prompt('Введіть ID цільової вершини:'), 10);

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !nodes.some((node) => node.id === fromId) ||
      !nodes.some((node) => node.id === toId)
    ) {
      alert('Некоректні ID вершин');
      return;
    }

    setEdges((prev) => [...prev, { from: fromId, to: toId }]);
  };

  const deleteEdge = () => {
    const fromId = parseInt(
      prompt('Введіть ID вихідної вершини для видалення ребра:'),
      10
    );
    const toId = parseInt(
      prompt('Введіть ID цільової вершини для видалення ребра:'),
      10
    );

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !edges.some((edge) => edge.from === fromId && edge.to === toId)
    ) {
      alert('Ребро не знайдено');
      return;
    }

    setEdges((prev) =>
      prev.filter(
        (edge) => !(edge.from === fromId && edge.to === toId)
      )
    );
  };

  const updateNodeState = useCallback((id, state) => {
    setNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === id ? { ...node, state } : node
      )
    );
    setCurrentNode(id);
  }, []);

  const startDFS = async () => {
    if (isRunning) return;

    const startNodeId = parseInt(
      prompt('Введіть ID початкової вершини:'),
      10
    );
    const endNodeId = parseInt(
      prompt('Введіть ID кінцевої вершини:'),
      10
    );

    if (
      isNaN(startNodeId) ||
      isNaN(endNodeId) ||
      !nodes.some((node) => node.id === startNodeId) ||
      !nodes.some((node) => node.id === endNodeId)
    ) {
      alert('Некоректний ID вершини');
      return;
    }

    setIsRunning(true);
    setIsPaused(false);
    setTargetNode(endNodeId);

    // Скидаємо стани вершин перед запуском
    setNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === endNodeId
          ? { ...node, state: 'unvisited' }
          : { ...node, state: 'unvisited' }
      )
    );

    try {
      await depthFirstSearch(
        nodes,
        edges,
        startNodeId,
        endNodeId,
        updateNodeState,
        1000
      );
    } catch (error) {
      console.error(error);
    } finally {
      setIsRunning(false);
      setCurrentNode(null);
    }
  };

  const pauseDFS = () => {
    // Для простоти реалізації паузи/продовження необхідно використовувати більш складну логіку,
    // наприклад, відмітки про паузу в алгоритмі. Тут ми просто реалізуємо кнопки без реальної паузи.
    alert('Функціонал паузи ще не реалізовано.');
  };

  const undoDFS = () => {
    // Реалізація відміни кроків потребує збереження історії станів.
    alert('Функціонал відміни кроків ще не реалізовано.');
  };

  const resetGraph = () => {
    setNodes((prev) =>
      prev.map((node) => ({
        ...node,
        state: 'unvisited',
      }))
    );
    setStepHistory([]);
    setCurrentNode(null);
    setTargetNode(null);
    setIsRunning(false);
    setIsPaused(false);
  };

  return (
    <div className="graph-container">
      <h1>Алгоритм Пошуку в Глибину (DFS)</h1>
      <Visualization nodes={nodes} edges={edges} />
      <Controls
        onAddNode={addNode}
        onDeleteNode={deleteNode}
        onAddEdge={addEdge}
        onDeleteEdge={deleteEdge}
        onStartDFS={startDFS}
        onPauseDFS={pauseDFS}
        onUndoDFS={undoDFS}
        onResetGraph={resetGraph}
        isRunning={isRunning}
        isPaused={isPaused}
      />
      <CurrentNode node={currentNode} />
      <RepresentationControls
        setView={setRepresentation}
        currentView={representation}
      />
      {representation === 'matrix' ? (
        <AdjacencyMatrix nodes={nodes} edges={edges} />
      ) : (
        <EdgeList edges={edges} />
      )}
      <Link to="/">
        <button>Home</button>
      </Link>
    </div>
  );
};

export default Graph;
