// src/components/Graph/algorithms/depthFirstSearch.js

export const depthFirstSearch = async (
  nodes,
  edges,
  startId,
  endId,
  updateNodeState,
  setStepHistory,
  setStack,
  delay = 1000,
  isPausedRef
) => {
  const visited = new Set();
  const stack = [startId];
  setStack([...stack]);

  while (stack.length > 0) {
    // Перевіряємо стан паузи
    if (isPausedRef.current) {
      await new Promise((resolve) => {
        const interval = setInterval(() => {
          if (!isPausedRef.current) {
            clearInterval(interval);
            resolve();
          }
        }, 100);
      });
    }

    const current = stack.pop();
    setStack([...stack]);

    if (!visited.has(current)) {
      visited.add(current);

      // Оновлюємо стан поточної вершини на "processing"
      updateNodeState(current, 'processing');

      // Затримка для анімації
      await new Promise((resolve) => setTimeout(resolve, delay));

      if (current === endId) {
        // Якщо знайшли цільову вершину, оновлюємо її стан на "target"
        updateNodeState(current, 'target');
        alert(`Знайдено кінцеву вершину V${endId}`);
        return;
      }

      // Отримуємо сусідів
      const neighbors = edges
        .filter((edge) => edge.from === current)
        .map((edge) => edge.to)
        .filter((neighbor) => !visited.has(neighbor));

      // Додаємо сусідів у стек
      neighbors.forEach((neighbor) => stack.push(neighbor));
      setStack([...stack]);

      // Оновлюємо стан поточної вершини на "visited"
      updateNodeState(current, 'visited');

      // Зберігаємо крок у історію
      setStepHistory((prevHistory) => [...prevHistory, current]);

      // Затримка для анімації
      await new Promise((resolve) => setTimeout(resolve, delay));
    }
  }

  alert('Цільова вершина не знайдена');
};
