import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AdjacencyMatrix from './AdjacencyMatrix/AdjacencyMatrix';
import EdgeList from './EdgeList/EdgeList';
import './Graph.css';

const Graph = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [currentNode, setCurrentNode] = useState(null);
  const [targetNode, setTargetNode] = useState(null); // Кінцева вершина
  const [stepHistory, setStepHistory] = useState([]); // Історія для кнопки "Назад"
  const [representation, setRepresentation] = useState('matrix');

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = [
      { id: 1, label: 'V1', x: 200, y: 200, state: 'unvisited' },
      { id: 2, label: 'V2', x: 300, y: 200, state: 'unvisited' },
      { id: 3, label: 'V3', x: 400, y: 200, state: 'unvisited' },
      { id: 4, label: 'V4', x: 200, y: 300, state: 'unvisited' },
      { id: 5, label: 'V5', x: 300, y: 300, state: 'unvisited' },
      { id: 6, label: 'V6', x: 400, y: 300, state: 'unvisited' },
    ];

    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 6 },
      { from: 1, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];

    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }

    const newNodeId = nodes.length + 1;
    const newX = 200 + (newNodeId % 3) * 100;
    const newY = 200 + Math.floor((newNodeId - 1) / 3) * 100;

    const newNode = {
      id: newNodeId,
      label: `V${newNodeId}`,
      x: newX,
      y: newY,
      state: 'unvisited',
    };

    setNodes((prev) => [...prev, newNode]);
  };

  const deleteNode = () => {
    const nodeId = parseInt(prompt('Введіть ID вершини для видалення:'), 10);
    if (isNaN(nodeId) || !nodes.some((node) => node.id === nodeId)) {
      alert('Некоректний ID вершини');
      return;
    }

    setNodes((prev) => prev.filter((node) => node.id !== nodeId));
    setEdges((prev) => prev.filter((edge) => edge.from !== nodeId && edge.to !== nodeId));
  };

  const addEdge = () => {
    const fromId = parseInt(prompt('Введіть ID вихідної вершини:'), 10);
    const toId = parseInt(prompt('Введіть ID цільової вершини:'), 10);

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !nodes.some((node) => node.id === fromId) ||
      !nodes.some((node) => node.id === toId)
    ) {
      alert('Некоректні ID вершин');
      return;
    }

    setEdges((prev) => [...prev, { from: fromId, to: toId }]);
  };

  const deleteEdge = () => {
    const fromId = parseInt(prompt('Введіть ID вихідної вершини для видалення ребра:'), 10);
    const toId = parseInt(prompt('Введіть ID цільової вершини для видалення ребра:'), 10);

    if (
      isNaN(fromId) ||
      isNaN(toId) ||
      !edges.some((edge) => edge.from === fromId && edge.to === toId)
    ) {
      alert('Ребро не знайдено');
      return;
    }

    setEdges((prev) => prev.filter((edge) => !(edge.from === fromId && edge.to === toId)));
  };

  const startDFS = async () => {
    if (isRunning) return;

    const startNodeId = parseInt(prompt('Введіть ID початкової вершини:'), 10);
    const endNodeId = parseInt(prompt('Введіть ID кінцевої вершини:'), 10);

    if (
      isNaN(startNodeId) ||
      isNaN(endNodeId) ||
      !nodes.some((node) => node.id === startNodeId) ||
      !nodes.some((node) => node.id === endNodeId)
    ) {
      alert('Некоректний ID вершини');
      return;
    }

    setIsRunning(true);
    setIsPaused(false);
    setTargetNode(endNodeId);
    const visited = new Set();
    const stack = [startNodeId];
    setStepHistory([startNodeId]); // Початкова вершина для кнопки "Назад"

    while (stack.length > 0) {
      while (isPaused) {
        await new Promise((resolve) => setTimeout(resolve, 100));
      }

      const current = stack.pop();
      setCurrentNode(current);

      setNodes((prev) =>
        prev.map((node) =>
          node.id === current ? { ...node, state: 'processing' } : node
        )
      );

      await new Promise((resolve) => setTimeout(resolve, 1000));

      if (current === endNodeId) {
        alert(`Знайдено кінцеву вершину V${endNodeId}`);
        setNodes((prev) =>
          prev.map((node) =>
            node.id === current ? { ...node, state: 'target' } : node
          )
        );
        break;
      }

      visited.add(current);

      const neighbors = edges
        .filter((edge) => edge.from === current && !visited.has(edge.to))
        .map((edge) => edge.to);

      neighbors.forEach((neighbor) => {
        if (!visited.has(neighbor)) {
          stack.push(neighbor);
        }
      });

      setStepHistory((prev) => [...prev, current]);
      setNodes((prev) =>
        prev.map((node) =>
          node.id === current ? { ...node, state: 'visited' } : node
        )
      );
    }

    setIsRunning(false);
    setCurrentNode(null);
  };

  const pauseDFS = () => {
    setIsPaused(!isPaused);
  };

  const undoDFS = () => {
    if (stepHistory.length <= 1) {
      alert('Немає попередніх кроків для відкату.');
      return;
    }

    const lastVisitedNode = stepHistory.pop();
    setStepHistory([...stepHistory]);

    setNodes((prev) =>
      prev.map((node) =>
        node.id === lastVisitedNode ? { ...node, state: 'unvisited' } : node
      )
    );
    setCurrentNode(stepHistory[stepHistory.length - 1] || null);
  };

  const resetGraph = () => {
    setNodes((prev) =>
      prev.map((node) => ({
        ...node,
        state: 'unvisited',
      }))
    );
    setStepHistory([]);
    setCurrentNode(null);
    setTargetNode(null);
    setIsRunning(false);
    setIsPaused(false);
  };

  const renderGraph = () => (
    <svg width="800" height="600">
      {edges.map((edge, index) => {
        const fromNode = nodes.find((node) => node.id === edge.from);
        const toNode = nodes.find((node) => node.id === edge.to);
        if (!fromNode || !toNode) return null;
        return (
          <line
            key={index}
            x1={fromNode.x}
            y1={fromNode.y}
            x2={toNode.x}
            y2={toNode.y}
            stroke="black"
            markerEnd="url(#arrowhead)"
          />
        );
      })}
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" />
        </marker>
      </defs>
      {nodes.map((node) => (
        <g key={node.id}>
          <circle
            cx={node.x}
            cy={node.y}
            r="20"
            fill={
              node.state === 'unvisited'
                ? 'gray'
                : node.state === 'processing'
                ? 'orange'
                : node.state === 'visited'
                ? 'black'
                : node.state === 'target'
                ? 'green'
                : 'gray'
            }
          />
          <text x={node.x} y={node.y + 5} textAnchor="middle" fill="white" fontSize="15">
            {node.label}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="graph-container">
      <h1>Алгоритм Глибокого Пошуку (DFS)</h1>
      <div className="graph-area">{renderGraph()}</div>
      <div className="representation-controls">
        <button onClick={() => setRepresentation('matrix')}>Матриця Суміжності</button>
        <button onClick={() => setRepresentation('edges')}>Список Ребер</button>
      </div>
      {representation === 'matrix' && <AdjacencyMatrix nodes={nodes} edges={edges} />}
      {representation === 'edges' && <EdgeList edges={edges} />}
      <div className="controls">
        <button onClick={addNode}>Додати Вершину</button>
        <button onClick={deleteNode}>Видалити Вершину</button>
        <button onClick={addEdge}>Додати Ребро</button>
        <button onClick={deleteEdge}>Видалити Ребро</button>
        <button onClick={startDFS}>Старт</button>
        <button onClick={pauseDFS} disabled={!isRunning}>
          {isPaused ? 'Продовжити' : 'Пауза'}
        </button>
        <button onClick={undoDFS} disabled={!isRunning}>
          Назад
        </button>
        <button onClick={resetGraph}>Забути пошук</button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
      {currentNode && <p>Поточна вершина: V{currentNode}</p>}
    </div>
  );
};

export default Graph;
