// src/components/Graph/adjacencyMatrix/AdjacencyMatrix.jsx

import React from 'react';
import './AdjacencyMatrix.css';

const AdjacencyMatrix = ({ nodes, edges }) => {
  const size = nodes.length;
  const matrix = Array.from({ length: size }, () => Array(size).fill(0));

  edges.forEach(edge => {
    const fromIndex = nodes.findIndex(n => n.id === edge.from);
    const toIndex = nodes.findIndex(n => n.id === edge.to);
    if (fromIndex !== -1 && toIndex !== -1) {
      matrix[fromIndex][toIndex] = 1;
    }
  });

  return (
    <div className="adjacency-matrix">
      <h3>Матриця Суміжності</h3>
      <table>
        <thead>
          <tr>
            <th></th>
            {nodes.map(node => (
              <th key={node.id}>{node.label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {matrix.map((row, i) => (
            <tr key={nodes[i].id}>
              <td>{nodes[i].label}</td>
              {row.map((val, j) => (
                <td
                  key={j}
                  className={val === 0 ? 'zero-cell' : 'one-cell'}
                >
                  {val}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdjacencyMatrix;
