import React from 'react';
import './AdjacencyList.css';

const AdjacencyList = ({ nodes, edges }) => {
  const adjacencyList = nodes.reduce((acc, node) => {
    acc[node.label] = [];
    return acc;
  }, {});

  edges.forEach((edge) => {
    const fromNode = nodes.find((node) => node.id === edge.from);
    const toNode = nodes.find((node) => node.id === edge.to);
    if (fromNode && toNode) {
      adjacencyList[fromNode.label].push(toNode.label);
    }
  });

  return (
    <div className="adjacency-list">
      <h3>Список Суміжності</h3>
      <ul>
        {Object.entries(adjacencyList).map(([key, value]) => (
          <li key={key}>
            {key}: {value.join(', ')}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdjacencyList;
