import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Graph.css';
import AdjacencyMatrix from './AdjacencyMatrix/AdjacencyMatrix';
import AdjacencyList from './AdjacencyList/AdjacencyList';
import EdgeList from './EdgeList/EdgeList';
import dfsAlgorithm from './dfsAlgorithm/dfsAlgorithm';

const Graph = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = Array.from({ length: 6 }, (_, i) => ({
      id: i + 1,
      label: `V${i + 1}`,
      x: Math.random() * 700 + 50,
      y: Math.random() * 500 + 50,
      state: 'unvisited',
    }));
    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];
    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const saveHistory = () => {
    setHistory([...history, { nodes: [...nodes], edges: [...edges] }]);
  };

  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }
    const newNode = {
      id: nodes.length + 1,
      label: `V${nodes.length + 1}`,
      x: Math.random() * 700 + 50,
      y: Math.random() * 500 + 50,
      state: 'unvisited',
    };
    saveHistory();
    setNodes([...nodes, newNode]);
  };

  const deleteNode = () => {
    const nodeId = parseInt(prompt('Введіть ID вершини для видалення:'), 10);
    if (isNaN(nodeId) || !nodes.find((node) => node.id === nodeId)) {
      alert('Введено некоректний ID');
      return;
    }
    saveHistory();
    setNodes(nodes.filter((node) => node.id !== nodeId));
    setEdges(edges.filter((edge) => edge.from !== nodeId && edge.to !== nodeId));
  };

  const startDFS = async () => {
    const startNodeLabel = prompt('Введіть початкову вершину (наприклад, V1):');
    const startNode = nodes.find((node) => node.label === startNodeLabel);
    if (!startNode) {
      alert('Початкова вершина не знайдена');
      return;
    }
    const visitedNodesState = [];
    await dfsAlgorithm(nodes, edges, startNode.id, visitedNodesState);
  };

  const undo = () => {
    if (history.length === 0) {
      alert('Немає попередніх станів для відкату.');
      return;
    }
    const lastState = history.pop();
    setNodes(lastState.nodes);
    setEdges(lastState.edges);
    setHistory(history);
  };

  const renderGraph = () => (
    <svg width="800" height="600">
      {edges.map((edge, index) => {
        const fromNode = nodes.find((node) => node.id === edge.from);
        const toNode = nodes.find((node) => node.id === edge.to);
        if (!fromNode || !toNode) return null;
        return (
          <line
            key={index}
            x1={fromNode.x}
            y1={fromNode.y}
            x2={toNode.x}
            y2={toNode.y}
            stroke="black"
            markerEnd="url(#arrowhead)"
          />
        );
      })}
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" />
        </marker>
      </defs>
      {nodes.map((node) => (
        <g key={node.id}>
          <circle cx={node.x} cy={node.y} r="20" fill="gray" />
          <text x={node.x} y={node.y + 5} textAnchor="middle" fill="white" fontSize="15">
            {node.label}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="graph-container">
      <h1>Алгоритм Глибокого Пошуку (DFS)</h1>
      <div className="graph-area">{renderGraph()}</div>
      <div className="controls">
        <button onClick={addNode}>Додати Вершину</button>
        <button onClick={deleteNode}>Видалити Вершину</button>
        <button onClick={startDFS}>Start</button>
        <button onClick={undo}>Назад</button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
    </div>
  );
};

export default Graph;
