import React from 'react';

const Visualization = ({ nodes, edges, target }) => (
  <svg width="800" height="600">
    {edges.map((edge, idx) => {
      const from = nodes.find(node => node.id === edge.from);
      const to = nodes.find(node => node.id === edge.to);
      if (!from || !to) return null;
      return (
        <line
          key={idx}
          x1={from.x}
          y1={from.y}
          x2={to.x}
          y2={to.y}
          stroke="black"
          markerEnd="url(#arrow)"
        />
      );
    })}
    <defs>
      <marker
        id="arrow"
        markerWidth="10"
        markerHeight="7"
        refX="10"
        refY="3.5"
        orient="auto"
      >
        <polygon points="0 0, 10 3.5, 0 7" />
      </marker>
    </defs>
    {nodes.map(node => (
      <g key={node.id}>
        <circle
          cx={node.x}
          cy={node.y}
          r="20"
          fill={
            node.id === target
              ? 'green'
              : node.state === 'unvisited'
              ? 'gray'
              : node.state === 'processing'
              ? 'orange'
              : node.state === 'visited'
              ? 'black'
              : 'gray'
          }
        />
        <text
          x={node.x}
          y={node.y + 5}
          textAnchor="middle"
          fill="white"
          fontSize="15"
        >
          {node.label}
        </text>
      </g>
    ))}
  </svg>
);

export default Visualization;
