export function* depthFirstSearch(nodes, edges, startId, targetId) {
  const visited = new Set();
  const stack = [startId];

  while (stack.length > 0) {
    const current = stack.pop();

    if (!visited.has(current)) {
      visited.add(current);
      yield { current, action: 'processing' };

      if (current === targetId) {
        yield { current, action: 'found' };
        return;
      }

      const neighbors = edges
        .filter(edge => edge.from === current)
        .map(edge => edge.to)
        .filter(neighbor => !visited.has(neighbor));

      neighbors.forEach(neighbor => stack.push(neighbor));

      yield { current, action: 'visited' };
    }
  }

  yield { current: null, action: 'completed' };
}
