import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AdjacencyMatrix from './AdjacencyMatrix/AdjacencyMatrix';
import AdjacencyList from './AdjacencyList/AdjacencyList';
import EdgeList from './EdgeList/EdgeList';
import dfsAlgorithm from './dfsAlgorithm/dfsAlgorithm'; // Використаємо це
import './Graph.css';

const Graph = () => {
  const [adjacencyList, setAdjacencyList] = useState({});
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [history, setHistory] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [currentNode, setCurrentNode] = useState(null); // Відобразимо стан вершини
  const [representation, setRepresentation] = useState('matrix');

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = [
      { id: 1, label: 'V1', x: 200, y: 200, state: 'unvisited' },
      { id: 2, label: 'V2', x: 300, y: 200, state: 'unvisited' },
      { id: 3, label: 'V3', x: 400, y: 200, state: 'unvisited' },
      { id: 4, label: 'V4', x: 200, y: 300, state: 'unvisited' },
      { id: 5, label: 'V5', x: 300, y: 300, state: 'unvisited' },
      { id: 6, label: 'V6', x: 400, y: 300, state: 'unvisited' },
    ];

    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 6 },
      { from: 1, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];

    const initialAdjacencyList = {
      1: [2, 4],
      2: [3],
      3: [6],
      4: [5],
      5: [6],
      6: [],
    };

    setNodes(initialNodes);
    setEdges(initialEdges);
    setAdjacencyList(initialAdjacencyList);
  };

  const startDFS = async () => {
    if (isRunning) return;

    const startNodeId = parseInt(prompt('Введіть ID початкової вершини:'), 10);
    if (isNaN(startNodeId) || !nodes.some((node) => node.id === startNodeId)) {
      alert('Некоректний ID початкової вершини');
      return;
    }

    setIsRunning(true);
    setIsPaused(false);

    const visited = new Set();
    const stack = [startNodeId];

    while (stack.length > 0 && !isPaused) {
      const current = stack.pop();
      setCurrentNode(current); // Оновлюємо поточну вершину

      setNodes((prev) =>
        prev.map((node) =>
          node.id === current ? { ...node, state: 'processing' } : node
        )
      );

      await new Promise((resolve) => setTimeout(resolve, 1000));

      visited.add(current);

      const neighbors = adjacencyList[current] || [];
      neighbors.forEach((neighbor) => {
        if (!visited.has(neighbor)) {
          stack.push(neighbor);
        }
      });

      setNodes((prev) =>
        prev.map((node) =>
          node.id === current ? { ...node, state: 'visited' } : node
        )
      );
    }

    setIsRunning(false);
    setCurrentNode(null); // Скидаємо поточну вершину
  };

  const pauseDFS = () => {
    setIsPaused(!isPaused);
  };

  const saveHistory = () => {
    setHistory((prev) => [
      ...prev,
      { nodes: [...nodes], edges: [...edges], adjacencyList: { ...adjacencyList } },
    ]);
  };

  const renderGraph = () => (
    <svg width="800" height="600">
      {edges.map((edge, index) => {
        const fromNode = nodes.find((node) => node.id === edge.from);
        const toNode = nodes.find((node) => node.id === edge.to);
        if (!fromNode || !toNode) return null;
        return (
          <line
            key={index}
            x1={fromNode.x}
            y1={fromNode.y}
            x2={toNode.x}
            y2={toNode.y}
            stroke="black"
            markerEnd="url(#arrowhead)"
          />
        );
      })}
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" />
        </marker>
      </defs>
      {nodes.map((node) => (
        <g key={node.id}>
          <circle
            cx={node.x}
            cy={node.y}
            r="20"
            fill={
              node.state === 'unvisited'
                ? 'gray'
                : node.state === 'processing'
                ? 'orange'
                : 'black'
            }
          />
          <text x={node.x} y={node.y + 5} textAnchor="middle" fill="white" fontSize="15">
            {node.label}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="graph-container">
      <h1>Алгоритм Глибокого Пошуку (DFS)</h1>
      <div className="graph-area">{renderGraph()}</div>
      <div className="representation-controls">
        <button onClick={() => setRepresentation('matrix')}>Матриця Суміжності</button>
        <button onClick={() => setRepresentation('list')}>Список Суміжності</button>
        <button onClick={() => setRepresentation('edges')}>Список Ребер</button>
      </div>
      {representation === 'matrix' && <AdjacencyMatrix nodes={nodes} edges={edges} />}
      {representation === 'list' && <AdjacencyList adjacencyList={adjacencyList} />}
      {representation === 'edges' && <EdgeList edges={edges} />}
      <div className="controls">
        <button onClick={startDFS}>Старт</button>
        <button onClick={pauseDFS} disabled={!isRunning}>
          {isPaused ? 'Продовжити' : 'Пауза'}
        </button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
      {currentNode && <p>Поточна вершина: V{currentNode}</p>}
    </div>
  );
};

export default Graph;
