import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Graph.css';
import dfsAlgorithm from './dfsAlgorithm/dfsAlgorithm';

const Graph = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [history, setHistory] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    initializeGraph();
  }, []);

  const initializeGraph = () => {
    const initialNodes = [
      { id: 1, label: 'V1', x: 300, y: 50, state: 'unvisited' },
      { id: 2, label: 'V2', x: 200, y: 150, state: 'unvisited' },
      { id: 3, label: 'V3', x: 400, y: 150, state: 'unvisited' },
      { id: 4, label: 'V4', x: 300, y: 250, state: 'unvisited' },
      { id: 5, label: 'V5', x: 200, y: 350, state: 'unvisited' },
      { id: 6, label: 'V6', x: 400, y: 350, state: 'unvisited' },
    ];

    const initialEdges = [
      { from: 1, to: 2 },
      { from: 2, to: 3 },
      { from: 3, to: 4 },
      { from: 4, to: 5 },
      { from: 5, to: 6 },
    ];

    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const saveHistory = () => {
    setHistory([...history, { nodes: [...nodes], edges: [...edges] }]);
  };

  const addNode = () => {
    if (nodes.length >= 15) {
      alert('Максимальна кількість вершин досягнута');
      return;
    }
    const spacingX = 100;
    const spacingY = 100;
    const newNodeId = nodes.length + 1;
    const newX = 300 + (newNodeId % 3 - 1) * spacingX;
    const newY = 50 + Math.floor((newNodeId - 1) / 3) * spacingY;

    const newNode = {
      id: newNodeId,
      label: `V${newNodeId}`,
      x: newX,
      y: newY,
      state: 'unvisited',
    };

    saveHistory();

    const updatedNodes = nodes.map((node) => ({
      ...node,
      x: node.x + 10,
      y: node.y + 10,
    }));

    setNodes([...updatedNodes, newNode]);
  };

  const deleteNode = () => {
    const nodeId = parseInt(prompt('Введіть ID вершини для видалення:'), 10);
    if (isNaN(nodeId) || !nodes.find((node) => node.id === nodeId)) {
      alert('Введено некоректний ID');
      return;
    }

    saveHistory();
    setNodes(nodes.filter((node) => node.id !== nodeId));
    setEdges(edges.filter((edge) => edge.from !== nodeId && edge.to !== nodeId));
  };

  const addEdge = () => {
    const fromId = parseInt(prompt('З якої вершини (ID)?'), 10);
    const toId = parseInt(prompt('До якої вершини (ID)?'), 10);

    if (isNaN(fromId) || isNaN(toId)) {
      alert('Некоректні ID вершин');
      return;
    }

    saveHistory();
    setEdges([...edges, { from: fromId, to: toId }]);
  };

  const startDFS = async () => {
    if (isRunning) return;

    const startNodeId = parseInt(prompt('Введіть ID початкової вершини:'), 10);
    const targetNodeId = parseInt(prompt('Введіть ID кінцевої вершини:'), 10);

    if (isNaN(startNodeId) || isNaN(targetNodeId)) {
      alert('Некоректні ID вершин');
      return;
    }

    setIsRunning(true);

    const updatedNodes = nodes.map((node) => ({
      ...node,
      state: node.id === startNodeId ? 'processing' : 'unvisited',
    }));
    setNodes(updatedNodes);

    await dfsAlgorithm(nodes, edges, startNodeId, setNodes);

    setIsRunning(false);
  };

  const pauseDFS = () => {
    setIsPaused(!isPaused);
  };

  const undo = () => {
    if (history.length === 0) {
      alert('Немає попередніх станів для відкату.');
      return;
    }

    const previousState = history.pop();
    setNodes(previousState.nodes);
    setEdges(previousState.edges);
  };

  const renderGraph = () => (
    <svg width="800" height="600">
      {edges.map((edge, index) => {
        const fromNode = nodes.find((node) => node.id === edge.from);
        const toNode = nodes.find((node) => node.id === edge.to);

        if (!fromNode || !toNode) return null;

        return (
          <line
            key={index}
            x1={fromNode.x}
            y1={fromNode.y}
            x2={toNode.x}
            y2={toNode.y}
            stroke="black"
            markerEnd="url(#arrowhead)"
          />
        );
      })}
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" />
        </marker>
      </defs>
      {nodes.map((node) => (
        <g key={node.id}>
          <circle
            cx={node.x}
            cy={node.y}
            r="20"
            fill={
              node.state === 'unvisited'
                ? 'gray'
                : node.state === 'processing'
                ? 'orange'
                : 'black'
            }
          />
          <text x={node.x} y={node.y + 5} textAnchor="middle" fill="white" fontSize="15">
            {node.label}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="graph-container">
      <h1>Алгоритм Глибокого Пошуку (DFS)</h1>
      <div className="graph-area">{renderGraph()}</div>
      <div className="controls">
        <button onClick={addNode}>Додати Вершину</button>
        <button onClick={deleteNode}>Видалити Вершину</button>
        <button onClick={addEdge}>Додати Ребро</button>
        <button onClick={startDFS}>Start</button>
        <button onClick={pauseDFS} disabled={!isRunning}>
          {isPaused ? 'Продовжити' : 'Пауза'}
        </button>
        <button onClick={undo}>Назад</button>
        <Link to="/">
          <button>Home</button>
        </Link>
      </div>
    </div>
  );
};

export default Graph;
